    //couple helpers
    #[inline]
    fn input_to_sorted_vec(s: &str) -> Vec<u16> {
        let mut v = s
            .split_terminator(",")
            .map(|n| u16::from_str_radix(n, 10).unwrap())
            .collect::<Vec<_>>();
        v.sort();
        v
    }

    #[inline]
    fn effectiveness(original_len: usize, compressed_len: usize) -> f64 {
        (original_len as f64 - compressed_len as f64) / (original_len as f64)
    }

